mapper.select:
SELECT

FROM t_time_modification_request
WHERE user_id = #{userId,jdbcType=BIGINT}
AND status = #{status,jdbcType=VARCHAR}
ORDER BY create_time DESC

SELECT

FROM t_time_modification_request t
LEFT JOIN t_time_record tr ON t.time_record_id = tr.id
WHERE tr.project_id = #{projectId,jdbcType=BIGINT}
AND t.status = #{status,jdbcType=VARCHAR}
ORDER BY t.create_time DESC

mapper.update:
UPDATE t_time_modification_request
time_record_id = #{timeRecordId,jdbcType=BIGINT}, user_id = #{userId,jdbcType=BIGINT}, new_hours = #{newHours,jdbcType=DOUBLE}, new_description = #{newDescription,jdbcType=VARCHAR}, reason = #{reason,jdbcType=VARCHAR}, status = #{status,jdbcType=VARCHAR}, reviewer_id = #{reviewerId,jdbcType=BIGINT}, review_comment = #{reviewComment,jdbcType=VARCHAR}, update_time = #{updateTime,jdbcType=TIMESTAMP},
WHERE id = #{id,jdbcType=BIGINT}
