mapper.select:
SELECT

FROM t_time_record
WHERE user_id = #{userId,jdbcType=BIGINT}
AND project_id = #{projectId,jdbcType=BIGINT}
ORDER BY work_date DESC, create_time DESC

SELECT

FROM t_time_record
WHERE user_id = #{userId,jdbcType=BIGINT}
AND work_date = #{workDate,jdbcType=VARCHAR}
ORDER BY create_time DESC

SELECT

FROM t_time_record
WHERE user_id = #{userId,jdbcType=BIGINT}
AND work_date BETWEEN #{startDate,jdbcType=VARCHAR} AND #{endDate,jdbcType=VARCHAR}
ORDER BY work_date DESC, create_time DESC

SELECT

FROM t_time_record
WHERE project_id = #{projectId,jdbcType=BIGINT}
AND work_date BETWEEN #{startDate,jdbcType=VARCHAR} AND #{endDate,jdbcType=VARCHAR}
ORDER BY work_date DESC, create_time DESC

mapper.update:
UPDATE t_time_record
user_id = #{userId,jdbcType=BIGINT}, project_id = #{projectId,jdbcType=BIGINT}, work_date = #{workDate,jdbcType=VARCHAR}, hours = #{hours,jdbcType=DOUBLE}, description = #{description,jdbcType=VARCHAR}, status = #{status,jdbcType=VARCHAR}, update_time = #{updateTime,jdbcType=TIMESTAMP},
WHERE id = #{id,jdbcType=BIGINT}
